
if Not Exists(Select * from SysColumns where [Name]= 'SectionTypeID' and ID = (Select [ID] from SysObjects where Name = 'LB_Section' )) begin    Alter Table LB_Section Add SectionTypeID bigint    EXEC sys.sp_addextendedproperty @name = N'MS_Description', @value = N'小组类型ID' , @level0type = N'SCHEMA',@level0name = N'dbo',    @level1type = N'TABLE',@level1name = N'LB_Section', @level2type = N'COLUMN',@level2name = N'SectionTypeID' end  Go if Not Exists(Select * from SysColumns where [Name]= 'SectionType' and ID = (Select [ID] from SysObjects where Name = 'LB_Section' )) begin    Alter Table LB_Section Add SectionType nvarchar(100)    EXEC sys.sp_addextendedproperty @name = N'MS_Description', @value = N'小组类型名称' , @level0type = N'SCHEMA',@level0name = N'dbo',    @level1type = N'TABLE',@level1name = N'LB_Section', @level2type = N'COLUMN',@level2name = N'SectionType' end   Go